import osinput_file_path = '/opt/cellframe-node/var/log/cellframe-node.log'output_file_path = 'output_file.txt'if os.path.exists(input_file_path):    with open(input_file_path, 'r', encoding='cp1251') as input_file:        lines = input_file.readlines()[-40000:]    with open(output_file_path, 'w') as output_file:        output_file.writelines(lines)        print(f"File {output_file_path} OK.")else:    print(f"File {input_file_path} not found.")